sys_script_include.script:
//*****Start of Script*****

//NOTE: the x_XXXX may need to be changed to match the name of your TheLibrary application name
var utils = x_35505_thelibrar.TheLibrary;

var TheApplicationAjax = Class.create();
TheApplicationAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    // Send the passed in JSON to be written to the System Log
    systemLogger: function() {
        var logjson = JSON.parse(this.getParameter('sysparm_logjson'));
        utils.parseAndWriteLog(logjson);
    },

    // Obtain the Address from TheLibrary Lookup table, and pass it back to
    // the client(browser)
    getAddress: function() {
        var name = this.getParameter('sysparm_username');
        return utils.getLookupRecord(name);
    },

    type: 'TheApplicationAjax'
});

//*****End of Script*****);
